package com.perscholas;

import java.util.Scanner;

public class Rounding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	  double x = 3.6;
	  System.out.println( Math.floor(2.675));
	  System.out.println(Math.ceil(5.4444));
	  System.out.println(Math.floorDiv(5, 3));
	  System.out.println(Math.exp(3));
	  System.out.println(Math.max(5, 6));
	  System.out.println(Math.nextDown(5));
	  System.out.println(Math.nextUp(8));
	  System.out.println(Math.round(5.3));
	  System.out. println(Math.rint(4));
	  System.out.println(Math.abs(5.7));
	  System.out.println(Math.random());
	  
         
         
	}

}
